import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { db } from "../firebase";
import { collection, query, where, orderBy, getDocs } from "firebase/firestore";

const QuizScreen = () => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);

  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const level = searchParams.get("level"); // "beginner", "intermediate", "advanced"

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const q = query(
          collection(db, "questions"),
          where("level", "==", level), // Firestore에서 level 필드로 필터링
          orderBy("id", "asc")
        );
        const querySnapshot = await getDocs(q);
        const fetchedQuestions = querySnapshot.docs.map((doc) => doc.data());
        setQuestions(fetchedQuestions);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching questions: ", error);
      }
    };

    fetchQuestions();
  }, [level]);

  if (loading) return <p>Loading questions...</p>;

  return (
    <div>
      <h1>{level === "beginner" ? "초급" : level === "intermediate" ? "중급" : "고급"} 퀴즈</h1>
      {questions.map((question, index) => (
        <div key={index}>
          <p>{question.question}</p>
          <p>{question.hint}</p>
        </div>
      ))}
    </div>
  );
};

export default QuizScreen;
