import React from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Levels.css";

const Levels = () => {
  const navigate = useNavigate();

  const levels = {
    beginner: [
      {
        id: "level-1",
        title: "일상 회화",
        description: "오늘은 어떤 공부를 할까?",
        image: "/img/easy2.png",
        keywords: ["일상 표현", "인사"],
      },
      // Other levels for beginner...
    ],
    intermediate: [
      {
        id: "level-5",
        title: "상황별 회화",
        description: "얼마에요? 현금으로 결제할게요.",
        image: "/img/normal2.png",
        keywords: ["상황 연습", "결제 표현"],
      },
      // Other levels for intermediate...
    ],
    advanced: [
      {
        id: "level-9",
        title: "비즈니스 영어",
        description: "We need to improve our synergy for better results.",
        image: "/img/hard2.png",
        keywords: ["비즈니스", "회의 표현"],
      },
      // Other levels for advanced...
    ],
  };

  const topLevels = {
    beginner: {
      id: "level-0",
      title: "초급",
      description: "일상 회화 : 일상에서 자주 사용하는 짧은 표현과 문장들",
      image: "/img/easy1.png",
    },
    intermediate: {
      id: "level-4",
      title: "중급",
      description:
        "상황별 대화 : 쇼핑, 여행, 레스토랑 등 다양한 상황에서의 대화연습",
      image: "/img/normal1.png",
    },
    advanced: {
      id: "level-8",
      title: "고급",
      description:
        "비즈니스 영어 : 비즈니스 상황에서 사용하는 영어, 발표 및 면접 연습",
      image: "/img/hard1.png",
    },
  };

  const renderTopLevel = (topLevel, category) => (
    <div className="level-card_level no-border_level top-level_level">
      <img
        src={topLevel.image}
        alt={topLevel.title}
        className="large-image_level"
      />
      <div className="level-text_level">
        <h2>{topLevel.title}</h2>
        <p className="title_description_level">{topLevel.description}</p>
        <button
          className="learn-button_level"
          onClick={() => navigate(`/quiz?level=${category}`)}
        >
          학습 시작
        </button>
      </div>
    </div>
  );
  

  const renderLevels = (category, topLevel) => (
    <section className="container_level">
      {renderTopLevel(topLevel, category)}
      <div className="grid-layout_level">
        {levels[category].map((level) => (
          <div
            key={level.id}
            className={`level-card_level ${
              level.spanFull ? "full-span_level" : ""
            }`}
          >
            <img
              src={level.image}
              alt={level.title}
              className="level-image_level"
            />
            <div className="level-text_level">
              <h3>{level.title}</h3>
              <p className="description_level">{level.description}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );

  return (
    <div>
      {renderLevels("beginner", topLevels.beginner)}
      {renderLevels("intermediate", topLevels.intermediate)}
      {renderLevels("advanced", topLevels.advanced)}
    </div>
  );
};

export default Levels;
